LoopsConditionalsClass Binding <pre><code>const items = ["Apple", "Banana", "Cherry"];

const template = html`
  &lt;ul&gt;
    ${items.map(item =&gt; html`&lt;li&gt;${item}&lt;/li&gt;`)}
  &lt;/ul&gt;
`;
</code></pre> <pre><code>const isLoading = true;

const template = html`
  ${isLoading 
    ? html`&lt;div&gt;Loading...&lt;/div&gt;`
    : html`&lt;div&gt;Loaded!&lt;/div&gt;`
  }
`;
</code></pre> <pre><code>const isActive = true;

const template = html`
  &lt;div class=${"item " + (isActive ? "active" : "")}&gt;
    Content
  &lt;/div&gt;
`;
</code></pre>
